import 'package:flutter/material.dart';
import 'package:gtrack_nartec/global/common/colors/app_colors.dart';
import 'package:intl/intl.dart';

class JobOrderCard extends StatelessWidget {
  final dynamic order;
  final String status;
  final VoidCallback onTap;

  const JobOrderCard({
    Key? key,
    required this.order,
    required this.status,
    required this.onTap,
  }) : super(key: key);

  String _formatDate(dynamic date) {
    if (date == null) return 'N/A';
    try {
      DateTime dateTime;
      if (date is String) {
        dateTime = DateTime.parse(date);
      } else if (date is DateTime) {
        dateTime = date;
      } else {
        return 'N/A';
      }
      return DateFormat('MMM dd, yyyy').format(dateTime);
    } catch (e) {
      return 'N/A';
    }
  }

  Color _getStatusColor(String status) {
    switch (status.toLowerCase()) {
      case 'active':
      case 'open':
        return Colors.green;
      case 'pending':
        return Colors.orange;
      case 'completed':
        return Colors.blue;
      case 'cancelled':
        return Colors.red;
      default:
        return AppColors.grey;
    }
  }

  @override
  Widget build(BuildContext context) {
    return Card(
      margin: const EdgeInsets.only(bottom: 12),
      elevation: 2,
      shape: RoundedRectangleBorder(
        borderRadius: BorderRadius.circular(12),
      ),
      child: InkWell(
        onTap: onTap,
        borderRadius: BorderRadius.circular(12),
        child: Padding(
          padding: const EdgeInsets.all(16),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              // Header row with job order number and status
              Row(
                mainAxisAlignment: MainAxisAlignment.spaceBetween,
                children: [
                  Expanded(
                    child: Text(
                      order.jobOrderMaster?.jobOrderNumber ?? 'N/A',
                      style: const TextStyle(
                        fontSize: 16,
                        fontWeight: FontWeight.bold,
                        color: AppColors.primary,
                      ),
                    ),
                  ),
                  Container(
                    padding: const EdgeInsets.symmetric(
                      horizontal: 8,
                      vertical: 4,
                    ),
                    decoration: BoxDecoration(
                      color: _getStatusColor(status).withOpacity(0.1),
                      borderRadius: BorderRadius.circular(16),
                      border: Border.all(
                        color: _getStatusColor(status),
                        width: 1,
                      ),
                    ),
                    child: Text(
                      status,
                      style: TextStyle(
                        fontSize: 12,
                        fontWeight: FontWeight.w500,
                        color: _getStatusColor(status),
                      ),
                    ),
                  ),
                ],
              ),

              const SizedBox(height: 12),

              // Product information
              if (order.jobOrderMaster?.product?.name != null)
                _buildInfoRow(
                  icon: Icons.inventory_2_outlined,
                  label: 'Product',
                  value: order.jobOrderMaster!.product!.name!,
                ),

              // Quantity information
              if (order.jobOrderMaster?.quantity != null)
                _buildInfoRow(
                  icon: Icons.numbers,
                  label: 'Quantity',
                  value: '${order.jobOrderMaster!.quantity} ${order.jobOrderMaster?.unit ?? ''}',
                ),

              // Start date
              if (order.jobOrderMaster?.startDate != null)
                _buildInfoRow(
                  icon: Icons.calendar_today_outlined,
                  label: 'Start Date',
                  value: _formatDate(order.jobOrderMaster!.startDate),
                ),

              // End date
              if (order.jobOrderMaster?.endDate != null)
                _buildInfoRow(
                  icon: Icons.event_outlined,
                  label: 'End Date',
                  value: _formatDate(order.jobOrderMaster!.endDate),
                ),

              // Customer information
              if (order.jobOrderMaster?.customer?.name != null)
                _buildInfoRow(
                  icon: Icons.person_outline,
                  label: 'Customer',
                  value: order.jobOrderMaster!.customer!.name!,
                ),

              // Priority or department
              if (order.jobOrderMaster?.priority != null)
                _buildInfoRow(
                  icon: Icons.priority_high,
                  label: 'Priority',
                  value: order.jobOrderMaster!.priority!,
                ),

              const SizedBox(height: 8),

              // Footer with created date
              Row(
                mainAxisAlignment: MainAxisAlignment.spaceBetween,
                children: [
                  Text(
                    'Created: ${_formatDate(order.createdAt)}',
                    style: TextStyle(
                      fontSize: 12,
                      color: AppColors.grey,
                    ),
                  ),
                  Icon(
                    Icons.arrow_forward_ios,
                    size: 16,
                    color: AppColors.grey,
                  ),
                ],
              ),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildInfoRow({
    required IconData icon,
    required String label,
    required String value,
  }) {
    return Padding(
      padding: const EdgeInsets.only(bottom: 8),
      child: Row(
        children: [
          Icon(
            icon,
            size: 16,
            color: AppColors.grey,
          ),
          const SizedBox(width: 8),
          Text(
            '$label: ',
            style: TextStyle(
              fontSize: 14,
              color: AppColors.grey,
              fontWeight: FontWeight.w500,
            ),
          ),
          Expanded(
            child: Text(
              value,
              style: const TextStyle(
                fontSize: 14,
                color: AppColors.primary,
                fontWeight: FontWeight.w400,
              ),
            ),
          ),
        ],
      ),
    );
  }
}
